import React from "react";
import { useSelector } from "react-redux";
import { Link, useParams, Navigate } from "react-router-dom";
import Card from "../cards/Card";
import ROUTES from "../../app/routes";
// import quiz selector
import { selectQuizzes } from './quizzesSlice';

//export default function Quiz() {
  //const quizzes = {}; // replace this with a call to your selector to get all the quizzes in state
  //const { quizId } = useParams();
  //const quiz = quizzes[quizId];

  export default function Quiz() {
    // Extract the quizId parameter from the URL
    const { quizId } = useParams();
    // Retrieve the full quizzes object from the state
    const quizzes = useSelector(selectQuizzes);
    // Find the specific quiz by id
    const quiz = quizzes[quizId];





  if(!quiz) {
    return <Navigate to={ROUTES.quizzesRoute()} replace/>
  }


  return (
    <section>
      <h1>{quiz.name}</h1>
      <ul className="cards-list">
        {quiz.cardIds.map((id) => (
          <Card key={id} id={id} />
        ))}
      </ul>
      <Link to={ROUTES.newQuizRoute()} className="button center">
        Create a New Quiz
      </Link>
    </section>
  );
}
